import { globalValues } from "@scripts/globals";
import { writeFileSync } from "fs";

const importValues = Object
	.keys(globalValues)
	.map((key) => `var ${key}: typeof import("@duplojs/core")["${key}"]`);

writeFileSync(
	"dist/globalThis.d.ts",
	`
/* eslint-disable */
/* prettier-ignore */
// @ts-nocheck
// noinspection JSUnusedGlobalSymbols
// Generated by @duplojs/core
export {};
declare global {
	${importValues.join(";\n\t")};
}
`,
);
